import React, { useState } from 'react';
import { createReview, getReviews } from '../services/api';
import { Review, SentimentAnalysis } from '../types/api';

const Home: React.FC = () => {
  const [review, setReview] = useState({
    movieTitle: '',
    reviewText: '',
    rating: 0
  });
  const [sentiment, setSentiment] = useState<SentimentAnalysis | null>(null);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      // First, create the review
      const newReview = await createReview(review);
      
      // Then fetch all reviews to update the list
      const reviews = await getReviews('');
      
      // Reset form
      setReview({
        movieTitle: '',
        reviewText: '',
        rating: 0
      });
      
      // Update sentiment
      setSentiment(newReview.sentiment);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'An error occurred');
    } finally {
      setLoading(false);
    }
  };

  const handleRatingChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = parseInt(e.target.value);
    if (!isNaN(value) && value >= 0 && value <= 5) {
      setReview({ ...review, rating: value });
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-3xl mx-auto">
        <div className="bg-white shadow sm:rounded-lg">
          <div className="px-4 py-5 sm:p-6">
            <h3 className="text-lg leading-6 font-medium text-gray-900">
              Write a Movie Review
            </h3>
            <form onSubmit={handleSubmit} className="mt-5">
              <div className="space-y-4">
                <div>
                  <label htmlFor="movieTitle" className="block text-sm font-medium text-gray-700">
                    Movie Title
                  </label>
                  <input
                    type="text"
                    id="movieTitle"
                    value={review.movieTitle}
                    onChange={(e) => setReview({ ...review, movieTitle: e.target.value })}
                    className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                    required
                  />
                </div>
                <div>
                  <label htmlFor="reviewText" className="block text-sm font-medium text-gray-700">
                    Review
                  </label>
                  <textarea
                    id="reviewText"
                    value={review.reviewText}
                    onChange={(e) => setReview({ ...review, reviewText: e.target.value })}
                    rows={4}
                    className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                    required
                  />
                </div>
                <div>
                  <label htmlFor="rating" className="block text-sm font-medium text-gray-700">
                    Rating (1-5)
                  </label>
                  <input
                    type="number"
                    min="0"
                    max="5"
                    value={review.rating}
                    onChange={handleRatingChange}
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
                  />
                </div>
              </div>
              <div className="mt-5">
                <button
                  type="submit"
                  disabled={loading}
                  className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                >
                  {loading ? 'Submitting...' : 'Submit Review'}
                </button>
              </div>
            </form>
            {error && (
              <div className="mt-4 text-red-600">
                {error}
              </div>
            )}
            {sentiment && (
              <div className="mt-4">
                <h4 className="text-sm font-medium text-gray-900">Sentiment Analysis:</h4>
                <p className="mt-1 text-sm text-gray-500">
                  {sentiment.analysis}
                </p>
                <p className="mt-1 text-sm text-gray-500">
                  Confidence: {(sentiment.sentiment * 100).toFixed(2)}%
                </p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
