import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { FiSearch, FiStar, FiChevronLeft, FiChevronRight } from 'react-icons/fi';
import Icon from '../components/Icon';
import LoadingSpinner from '../components/LoadingSpinner';
import { movieDatabase } from '../data/movieDatabase';

interface Movie {
  id: string;
  title: string;
  image: string;
  year: string;
  rating: number;
  genre?: string[];
  description?: string;
}

const Movies: React.FC = () => {
  const [movies, setMovies] = useState<Movie[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [searchQuery, setSearchQuery] = useState<string>('');
  const [filteredMovies, setFilteredMovies] = useState<Movie[]>([]);
  const [selectedGenre, setSelectedGenre] = useState<string>('');

  // For pagination
  const [currentPage, setCurrentPage] = useState<number>(1);
  const moviesPerPage = 12;

  useEffect(() => {
    const fetchMovies = async () => {
      try {
        setLoading(true);
        // Use the movie database
        setMovies(movieDatabase);
        setFilteredMovies(movieDatabase);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching movies:', error);
        setLoading(false);
      }
    };
    
    fetchMovies();
  }, []);

  useEffect(() => {
    let filtered = movies;
    
    // Apply search filter
    if (searchQuery.trim() !== '') {
      filtered = filtered.filter(movie => 
        movie.title.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }
    
    // Apply genre filter
    if (selectedGenre) {
      filtered = filtered.filter(movie => 
        movie.genre?.includes(selectedGenre)
      );
    }
    
    setFilteredMovies(filtered);
    setCurrentPage(1);
  }, [searchQuery, selectedGenre, movies]);

  // Calculate pagination
  const indexOfLastMovie = currentPage * moviesPerPage;
  const indexOfFirstMovie = indexOfLastMovie - moviesPerPage;
  const currentMovies = filteredMovies.slice(indexOfFirstMovie, indexOfLastMovie);
  const totalPages = Math.ceil(filteredMovies.length / moviesPerPage);

  const handlePageChange = (pageNumber: number) => {
    setCurrentPage(pageNumber);
    window.scrollTo(0, 0);
  };

  // Get unique genres from all movies
  const allGenres = Array.from(
    new Set(movies.flatMap(movie => movie.genre || []))
  ).sort();

  return (
    <div className="container mx-auto py-8 px-4">
      <h1 className="text-3xl font-bold mb-8 text-gray-800">Discover Movies</h1>
      
      {/* Search and Filters */}
      <div className="mb-8">
        <div className="relative mb-4">
          <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
            <Icon icon={FiSearch} size={20} className="text-gray-400" />
          </div>
          <input
            type="text"
            className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            placeholder="Search movies..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </div>
        
        {/* Genre Filter */}
        <div className="flex flex-wrap gap-2">
          <button
            onClick={() => setSelectedGenre('')}
            className={`px-4 py-2 rounded-full text-sm ${
              selectedGenre === '' 
                ? 'bg-blue-600 text-white' 
                : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
            }`}
          >
            All
          </button>
          {allGenres.map((genre) => (
            <button
              key={genre}
              onClick={() => setSelectedGenre(genre)}
              className={`px-4 py-2 rounded-full text-sm ${
                selectedGenre === genre 
                  ? 'bg-blue-600 text-white' 
                  : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
              }`}
            >
              {genre}
            </button>
          ))}
        </div>
      </div>
      
      {loading ? (
        <div className="flex justify-center items-center h-64">
          <LoadingSpinner />
        </div>
      ) : (
        <>
          {filteredMovies.length === 0 ? (
            <div className="text-center py-12">
              <p className="text-xl text-gray-600">No movies found matching your search.</p>
            </div>
          ) : (
            <>
              {/* Movies Grid */}
              <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-8">
                {currentMovies.map(movie => (
                  <Link
                    key={movie.id}
                    to={`/movie/${movie.id}`}
                    className="bg-white rounded-xl shadow-md overflow-hidden hover:shadow-lg transition transform hover:-translate-y-1"
                  >
                    <div className="relative h-64">
                      <img
                        src={movie.image}
                        alt={movie.title}
                        className="w-full h-full object-cover"
                      />
                      <div className="absolute top-2 right-2 bg-blue-600 text-white rounded-full px-2 py-1 text-sm flex items-center">
                        <Icon icon={FiStar} size={16} className="mr-1" />
                        {movie.rating.toFixed(1)}
                      </div>
                    </div>
                    <div className="p-4">
                      <h2 className="font-bold text-lg mb-1 line-clamp-1">{movie.title}</h2>
                      <p className="text-gray-500 text-sm mb-2">{movie.year}</p>
                      <div className="flex flex-wrap gap-1 mb-2">
                        {movie.genre?.map((genre, index) => (
                          <span key={index} className="px-2 py-1 bg-gray-100 text-gray-700 rounded-full text-xs">
                            {genre}
                          </span>
                        ))}
                      </div>
                      <p className="text-gray-700 text-sm line-clamp-3">{movie.description}</p>
                    </div>
                  </Link>
                ))}
              </div>

              {/* Pagination */}
              {totalPages > 1 && (
                <div className="flex justify-center mt-8">
                  <nav className="flex items-center space-x-2">
                    <button
                      onClick={() => handlePageChange(currentPage - 1)}
                      disabled={currentPage === 1}
                      className="p-2 rounded-lg bg-gray-100 text-gray-600 hover:bg-gray-200 disabled:opacity-50 disabled:cursor-not-allowed"
                    >
                      <Icon icon={FiChevronLeft} size={20} />
                    </button>
                    
                    {Array.from({ length: totalPages }, (_, i) => i + 1).map((page) => (
                      <button
                        key={page}
                        onClick={() => handlePageChange(page)}
                        className={`w-10 h-10 rounded-lg ${
                          currentPage === page
                            ? 'bg-blue-600 text-white'
                            : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                        }`}
                      >
                        {page}
                      </button>
                    ))}
                    
                    <button
                      onClick={() => handlePageChange(currentPage + 1)}
                      disabled={currentPage === totalPages}
                      className="p-2 rounded-lg bg-gray-100 text-gray-600 hover:bg-gray-200 disabled:opacity-50 disabled:cursor-not-allowed"
                    >
                      <Icon icon={FiChevronRight} size={20} />
                    </button>
                  </nav>
                </div>
              )}
            </>
          )}
        </>
      )}
    </div>
  );
};

export default Movies;
