import React from 'react';
import { Link } from 'react-router-dom';

interface Review {
  id: string;
  movieTitle: string;
  reviewText: string;
  rating: number;
  sentiment: string;
  createdAt: string;
  user: {
    id: string;
    name: string;
  };
}

interface ReviewCardProps {
  review: Review;
}

const ReviewCard: React.FC<ReviewCardProps> = ({ review }) => {
  const sentimentColor = {
    positive: 'text-green-600',
    negative: 'text-red-600',
    neutral: 'text-gray-600'
  }[review.sentiment.toLowerCase()] || 'text-gray-600';

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  return (
    <Link
      to={`/review/${review.id}`}
      className="block p-6 bg-white rounded-lg shadow hover:shadow-md transition-shadow"
    >
      <div className="space-y-4">
        <div className="flex justify-between items-start">
          <h3 className="text-lg font-semibold text-gray-900 line-clamp-1">
            {review.movieTitle}
          </h3>
          <div className="flex items-center space-x-1">
            <span className="text-yellow-500">★</span>
            <span className="text-gray-600">{review.rating}/10</span>
          </div>
        </div>

        <p className="text-gray-600 line-clamp-3">{review.reviewText}</p>

        <div className="flex justify-between items-center text-sm">
          <span className="text-gray-500">
            By {review.user.name} • {formatDate(review.createdAt)}
          </span>
          <span className={`font-medium ${sentimentColor}`}>
            {review.sentiment}
          </span>
        </div>
      </div>
    </Link>
  );
};

export default ReviewCard;
