import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import { getMovie, getReviews } from '../services/api';
import LoadingSpinner from '../components/LoadingSpinner';
import { FiArrowLeft, FiStar, FiEdit, FiMessageSquare, FiClock } from 'react-icons/fi';
import Icon from '../components/Icon';
import { motion } from 'framer-motion';

interface Movie {
  _id: string;
  title: string;
  year: string;
  genre: string;
  imageUrl: string;
  description: string;
  tmdbId?: number;
  reviewCount: number;
  avgRating: number;
  reviews: Review[];
}

interface Review {
  _id: string;
  movieId: string;
  movieTitle: string;
  reviewText: string;
  rating: number;
  sentiment: string;
  createdAt: string;
  userName?: string;
}

const MovieDetail: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const [movie, setMovie] = useState<Movie | null>(null);
  const [reviews, setReviews] = useState<Review[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        if (!id) throw new Error('Movie ID is required');

        const movieData = await getMovie(id);
        setMovie(movieData);

        const reviewsData = await getReviews({ movieId: id });
        setReviews(reviewsData.reviews || []);
      } catch (err) {
        console.error('Error fetching movie:', err);
        setError(err instanceof Error ? err.message : 'Failed to load movie');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [id]);

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const formatRuntime = (minutes: number) => {
    if (!minutes) return 'Runtime not available';
    const hours = Math.floor(minutes / 60);
    const remainingMinutes = minutes % 60;
    return `${hours}h ${remainingMinutes}m`;
  };

  const getSentimentClass = (sentiment: string) => {
    switch (sentiment.toLowerCase()) {
      case 'positive':
        return 'bg-green-100 text-green-800';
      case 'negative':
        return 'bg-red-100 text-red-800';
      default:
        return 'bg-yellow-100 text-yellow-800';
    }
  };

  if (loading) {
    return <LoadingSpinner />;
  }

  if (error || !movie) {
    return (
      <div className="min-h-screen flex flex-col items-center justify-center p-4">
        <h1 className="text-2xl font-bold text-gray-800 mb-4">Oops! Something went wrong</h1>
        <p className="text-gray-600 mb-8">The movie you're looking for doesn't exist or has been removed.</p>
        <Link to="/movies" className="text-blue-600 hover:underline flex items-center justify-center">
          <Icon icon={FiArrowLeft} size={20} className="mr-2" /> Back to Movies
        </Link>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="bg-white rounded-lg shadow-lg overflow-hidden">
          <div className="md:flex">
            {/* Movie Poster */}
            <div className="md:flex-shrink-0">
              <img
                className="h-96 w-full object-cover md:w-96"
                src={movie.imageUrl}
                alt={movie.title}
                onError={(e) => {
                  const target = e.target as HTMLImageElement;
                  target.src = '/placeholder.png';
                }}
              />
            </div>

            {/* Movie Details */}
            <div className="p-8">
              <div className="uppercase tracking-wide text-sm text-indigo-500 font-semibold">
                {movie.genre}
              </div>
              <h1 className="mt-1 text-4xl font-bold text-gray-900">{movie.title}</h1>
              <div className="mt-2 flex items-center text-sm text-gray-500 space-x-4">
                <span>{formatDate(movie.year)}</span>
                <span>•</span>
                <span>{formatRuntime(movie.tmdbId || 0)}</span>
                <span>•</span>
                <div className="flex items-center">
                  <Icon icon={FiStar} size={16} className="mr-1 text-yellow-400" />
                  <span>{movie.avgRating.toFixed(1)}</span>
                </div>
              </div>
              <p className="mt-4 text-gray-500">{movie.description}</p>

              {/* Action Buttons */}
              <div className="mt-6 flex items-center space-x-4">
                <Link
                  to={`/movies/${movie._id}/review`}
                  className="flex items-center px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg transition"
                >
                  <Icon icon={FiEdit} size={18} className="mr-2" />
                  Write a Review
                </Link>
              </div>
            </div>
          </div>

          {/* Reviews Section */}
          <div className="border-t border-gray-200 px-8 py-6">
            <h2 className="text-2xl font-bold text-gray-900 mb-6">Reviews</h2>
            {reviews.length === 0 ? (
              <div className="bg-gray-50 rounded-lg p-8 text-center">
                <Icon icon={FiMessageSquare} size={36} className="mx-auto text-gray-400 mb-3" />
                <h3 className="text-xl font-semibold text-gray-700 mb-2">No Reviews Yet</h3>
                <p className="text-gray-600 mb-4">Be the first to share your thoughts on this movie!</p>
                <Link 
                  to={`/movies/${movie._id}/review`}
                  className="inline-flex items-center px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg transition"
                >
                  Write a Review
                </Link>
              </div>
            ) : (
              <div className="space-y-6">
                {reviews.map((review) => (
                  <motion.div
                    key={review._id}
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    className="bg-gray-50 rounded-lg p-6"
                  >
                    <div className="flex items-center justify-between mb-4">
                      <div className="flex items-center space-x-4">
                        <div className="flex-shrink-0">
                          <div className="w-10 h-10 rounded-full bg-gradient-to-r from-blue-500 to-purple-500 flex items-center justify-center text-white font-semibold">
                            {review.userName?.[0] || 'A'}
                          </div>
                        </div>
                        <div>
                          <div className="font-semibold text-gray-800">{review.userName || 'Anonymous User'}</div>
                          <div className="text-sm text-gray-500 flex items-center">
                            <Icon icon={FiClock} size={14} className="mr-1" />
                            {formatDate(review.createdAt)}
                          </div>
                        </div>
                      </div>
                      <div className="flex items-center space-x-2">
                        <div className="flex items-center bg-blue-100 text-blue-800 px-2 py-1 rounded">
                          <Icon icon={FiStar} size={14} className="mr-1" />
                          <span>{review.rating}/5</span>
                        </div>
                        <div className={`px-2 py-1 rounded text-sm ${getSentimentClass(review.sentiment)}`}>
                          {review.sentiment}
                        </div>
                      </div>
                    </div>
                    <p className="text-gray-700">{review.reviewText}</p>
                  </motion.div>
                ))}
              </div>
            )}
          </div>

          {/* Side Information */}
          <div className="border-t border-gray-200 px-8 py-6 bg-gray-50">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <div>
                <h3 className="text-lg font-semibold text-gray-900 mb-2">Movie Details</h3>
                <div className="space-y-4">
                  <div>
                    <span className="text-gray-500">Release Date:</span>
                    <div className="text-gray-800 font-medium">{formatDate(movie.year)}</div>
                  </div>
                  <div>
                    <span className="text-gray-500">Runtime:</span>
                    <div className="text-gray-800 font-medium">{formatRuntime(movie.tmdbId || 0)}</div>
                  </div>
                  <div>
                    <span className="text-gray-500">User Rating:</span>
                    <div className="flex items-center mt-1">
                      <Icon icon={FiStar} size={16} className="text-yellow-400 mr-1" />
                      <span className="text-gray-800 font-medium">{movie.avgRating.toFixed(1)}/10</span>
                    </div>
                  </div>
                </div>
              </div>
            </div>
            <div className="mt-8">
              <Link
                to="/movies"
                className="block w-full py-2 bg-white text-blue-700 rounded-lg text-center font-medium hover:bg-blue-50 transition"
              >
                <Icon icon={FiStar} size={18} className="inline mr-2" />
                Discover More Movies
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MovieDetail;
