import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import { createReview } from '../services/api';
import { useAuth } from '../context/AuthContext';
import { FiHome } from '../components/icons';
import IconWrapper from '../components/IconWrapper';

interface ResultLocationState {
  movieTitle: string;
  reviewText: string;
  rating: number;
  sentiment: string;
  detailedSentiment?: string;
  confidence: number;
}

const ResultPage: React.FC = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { isAuthenticated } = useAuth();
  const [saving, setSaving] = useState(false);
  const [saved, setSaved] = useState(false);
  const [error, setError] = useState('');
  const [autoSaveAttempted, setAutoSaveAttempted] = useState(false);
  
  const { movieTitle, reviewText, rating, sentiment, detailedSentiment, confidence } = 
    (location.state as ResultLocationState) || {
      movieTitle: '',
      reviewText: '',
      rating: 5,
      sentiment: '',
      detailedSentiment: '',
      confidence: 0,
    };

  // Display the detailed sentiment if available, otherwise use the base sentiment
  const displaySentiment = detailedSentiment || sentiment;

  // If no review text, redirect back to home
  useEffect(() => {
    if (!reviewText || !movieTitle) {
      navigate('/');
    }
  }, [reviewText, movieTitle, navigate]);

  // Check if review length is valid
  useEffect(() => {
    if (reviewText && reviewText.trim().length < 10) {
      setError('Review text must be at least 10 characters long.');
    }
  }, [reviewText]);

  // Auto-save review if authenticated
  useEffect(() => {
    if (!autoSaveAttempted && isAuthenticated && reviewText && movieTitle && sentiment && !error) {
      handleSaveReview(true);
      setAutoSaveAttempted(true);
    }
  }, [isAuthenticated, reviewText, movieTitle, sentiment, error]);

  const handleSaveReview = async (isAutoSave = false) => {
    if (!isAuthenticated) {
      navigate('/login', { state: { from: window.location.pathname } });
      return;
    }

    setSaving(true);
    setError('');
    
    try {
      // Save the review with the sentiment already analyzed
      await createReview({
        movieId: "placeholder-id", // You would normally get this from actual data
        movieTitle,
        reviewText,
        rating,
      });
      
      setSaved(true);
      
      // Only redirect automatically if it's a manual save
      if (!isAutoSave) {
        setTimeout(() => navigate('/dashboard'), 1500);
      }
    } catch (err) {
      console.error('Error saving review:', err);
      setError(err instanceof Error ? err.message : 'Failed to save review');
    } finally {
      setSaving(false);
    }
  };

  const getSentimentColor = () => {
    if (displaySentiment.includes('positive')) {
      return 'text-green-600';
    } else if (displaySentiment.includes('negative')) {
      return 'text-red-600';
    } else {
      return 'text-blue-600';
    }
  };

  const getSentimentBgColor = () => {
    if (displaySentiment.includes('positive')) {
      return 'bg-green-100';
    } else if (displaySentiment.includes('negative')) {
      return 'bg-red-100';
    } else {
      return 'bg-blue-100';
    }
  };

  const formatConfidence = (confidence: number) => {
    return Math.round(confidence * 100) + '%';
  };

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
      className="max-w-4xl mx-auto px-4 py-20"
    >
      <motion.div
        initial={{ y: 20, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        className="bg-white rounded-xl p-8 shadow-xl"
      >
        <h1 className="text-3xl font-bold mb-6 text-center bg-gradient-to-r from-indigo-600 to-purple-600 text-transparent bg-clip-text">
          Sentiment Analysis Results
        </h1>

        <div className="mb-6">
          <h2 className="text-xl font-semibold mb-3 text-gray-700">Movie</h2>
          <div className="bg-gray-50 p-4 rounded-lg border border-gray-200 text-gray-800">
            {movieTitle} - {rating} Star{rating !== 1 ? 's' : ''}
          </div>
        </div>

        <div className="mb-8">
          <h2 className="text-xl font-semibold mb-3 text-gray-700">Your Review</h2>
          <div className="bg-gray-50 p-6 rounded-lg border border-gray-200 italic text-gray-700">
            "{reviewText}"
          </div>
        </div>

        <div className="grid md:grid-cols-2 gap-8 mb-6">
          <div className="bg-white p-6 rounded-lg border border-gray-200 shadow-sm">
            <h3 className="text-lg font-semibold mb-4 text-gray-700">Sentiment</h3>
            <div className={`text-3xl font-bold ${getSentimentColor()} capitalize`}>
              {displaySentiment}
            </div>
          </div>

          <div className="bg-white p-6 rounded-lg border border-gray-200 shadow-sm">
            <h3 className="text-lg font-semibold mb-4 text-gray-700">Confidence</h3>
            <div className="flex items-center">
              <div className="flex-1 bg-gray-200 rounded-full h-4">
                <div
                  className={`h-4 rounded-full ${getSentimentBgColor()}`}
                  style={{ width: formatConfidence(confidence) }}
                ></div>
              </div>
              <span className="ml-4 text-lg font-bold">{formatConfidence(confidence)}</span>
            </div>
          </div>
        </div>

        {error && (
          <div className="text-red-500 text-sm bg-red-50 p-2 rounded mb-6">
            {error}
          </div>
        )}

        {saved && (
          <div className="text-green-500 text-sm bg-green-50 p-2 rounded mb-6">
            Review saved successfully! {!isAuthenticated && "View all your reviews on the dashboard."}
          </div>
        )}

        <div className="flex flex-col md:flex-row justify-center space-y-4 md:space-y-0 md:space-x-4">
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={() => navigate('/')}
            className="px-8 py-3 rounded-full text-indigo-600 font-medium border border-indigo-600 hover:bg-indigo-50"
          >
            Analyze Another Review
          </motion.button>
          
          {isAuthenticated && !saved && (
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => handleSaveReview(false)}
              disabled={saving}
              className={`px-8 py-3 rounded-full text-white font-medium ${
                saving
                  ? 'bg-gray-400 cursor-not-allowed'
                  : 'bg-gradient-to-r from-indigo-600 to-purple-600 hover:from-indigo-700 hover:to-purple-700 shadow-lg'
              }`}
            >
              {saving ? 'Saving...' : 'Save Review'}
            </motion.button>
          )}
          
          {isAuthenticated && saved && (
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => navigate('/dashboard')}
              className="px-8 py-3 rounded-full text-white font-medium bg-gradient-to-r from-indigo-600 to-purple-600 hover:from-indigo-700 hover:to-purple-700 shadow-lg"
            >
              View Dashboard
            </motion.button>
          )}
          
          {!isAuthenticated && (
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => navigate('/login', { state: { from: window.location.pathname } })}
              className="px-8 py-3 rounded-full text-white font-medium bg-gradient-to-r from-indigo-600 to-purple-600 hover:from-indigo-700 hover:to-purple-700 shadow-lg"
            >
              Login to Save Review
            </motion.button>
          )}
        </div>
      </motion.div>
    </motion.div>
  );
};

export default ResultPage;
