import axios, { AxiosError } from 'axios';

// Define the error response type
interface ApiErrorResponse {
  message: string;
}

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';
const ML_API_URL = process.env.REACT_APP_ML_API_URL || 'http://localhost:6000';

// Create axios instance with base URL
const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Add token to requests if available
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// Add response interceptor for error handling
api.interceptors.response.use(
  (response) => response,
  (error: AxiosError<ApiErrorResponse>) => {
    console.error('API Error:', {
      status: error.response?.status,
      data: error.response?.data,
      message: error.message
    });
    
    // Handle 401 Unauthorized errors
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      throw new Error('Session expired. Please log in again.');
    }
    
    if (error.response?.data?.message) {
      throw new Error(error.response.data.message);
    }
    throw error;
  }
);

// Auth API
export const login = async (email: string, password: string) => {
  try {
    // Try development mode login if regular login fails
    try {
      const response = await api.post<{ token: string; user: any }>('/auth/login', 
        { email, password }
      );
      localStorage.setItem('token', response.data.token);
      return response.data;
    } catch (err) {
      console.log('Regular login failed, trying development login...');
      try {
        // Try the X-Dev-Mode header approach first
        const devResponse = await axios.post<{ token: string; user: any }>(
          `${API_URL}/auth/login`,
          { email, password },
          { 
            headers: { 
              'Content-Type': 'application/json',
              'X-Dev-Mode': 'true'
            } 
          }
        );
        localStorage.setItem('token', devResponse.data.token);
        return devResponse.data;
      } catch (devErr) {
        // As a last resort, try the dedicated dev-login endpoint
        console.log('Dev mode login failed, trying dedicated dev login endpoint...');
        const devLoginResponse = await axios.post<{ token: string; user: any }>(
          `${API_URL}/auth/dev-login`,
          { email, name: email.split('@')[0] }
        );
        localStorage.setItem('token', devLoginResponse.data.token);
        return devLoginResponse.data;
      }
    }
  } catch (error) {
    console.error('Login error:', error);
    throw error;
  }
};

export const googleLogin = async (tokenId: string) => {
  try {
    console.log('Attempting Google login with token:', tokenId.substring(0, 10) + '...');
    const response = await api.post<{ token: string; user: any }>('/auth/google', { tokenId });
    localStorage.setItem('token', response.data.token);
    return response.data;
  } catch (error) {
    console.error('Google login error:', error);
    throw error;
  }
};

export const register = async (name: string, email: string, password: string) => {
  try {
    console.log('Registering user:', { name, email });
    const response = await api.post<{ token: string; user: any }>('/auth/register', {
      name: name.trim(),
      email: email.trim().toLowerCase(),
      password
    });
    console.log('Registration successful:', response.data);
    if (response.data.token) {
      localStorage.setItem('token', response.data.token);
    }
    return response.data;
  } catch (error) {
    console.error('Registration error:', error);
    if (error instanceof AxiosError) {
      if (error.response?.status === 409) {
        throw new Error('A user with this email already exists. Please try logging in instead.');
      }
      if (error.response?.data?.message) {
        throw new Error(error.response.data.message);
      }
    }
    throw new Error('Registration failed. Please try again.');
  }
};

// Users API
export const getUsers = async () => {
  const response = await api.get('/users');
  return response.data;
};

export const getCurrentUser = async () => {
  const response = await api.get('/users/me');
  return response.data;
};

export const updateUserRole = async (userId: string, role: string) => {
  const response = await api.patch(`/users/${userId}/role`, { role });
  return response.data;
};

export const deleteUser = async (userId: string) => {
  const response = await api.delete(`/users/${userId}`);
  return response.data;
};

// Movies API
export const getAllMovies = async () => {
  const response = await api.get('/movies');
  return response.data;
};

export const getMovie = async (id: string) => {
  const response = await api.get(`/movies/${id}`);
  return response.data;
};

export const searchMovies = async (query: string) => {
  const response = await api.get(`/movies/search?query=${encodeURIComponent(query)}`);
  return response.data;
};

export const addMovie = async (movie: {
  title: string;
  year: string;
  genre: string;
  imageUrl?: string;
  description?: string;
}) => {
  const response = await api.post('/movies', movie);
  return response.data;
};

export const updateMovie = async (
  id: string,
  movie: {
    title?: string;
    year?: string;
    genre?: string;
    imageUrl?: string;
    description?: string;
  }
) => {
  const response = await api.put(`/movies/${id}`, movie);
  return response.data;
};

export const deleteMovie = async (id: string) => {
  const response = await api.delete(`/movies/${id}`);
  return response.data;
};

// Updated Reviews API to work with movies
export const createReview = async (review: {
  movieId: string;
  movieTitle: string;
  reviewText: string;
  rating: number;
}) => {
  try {
    console.log('Starting review creation process...');
    
    // Make sure the review text is at least 10 characters
    if (review.reviewText.trim().length < 10) {
      throw new Error('Review text must be at least 10 characters long');
    }
    
    // First, get sentiment analysis from ML API
    console.log('Sending text to ML API for sentiment analysis:', review.reviewText.substring(0, 50) + '...');
    const sentimentResponse = await axios.post(`${ML_API_URL}/predict`, {
      text: review.reviewText,
    });
    console.log('Received sentiment analysis:', sentimentResponse.data);

    // Normalize the sentiment value to match what the backend accepts
    let normalizedSentiment = 'neutral';
    const rawSentiment = sentimentResponse.data.sentiment.toLowerCase();
    
    if (rawSentiment.includes('positive')) {
      normalizedSentiment = 'positive';
    } else if (rawSentiment.includes('negative')) {
      normalizedSentiment = 'negative';
    }

    // Then create review with normalized sentiment
    const reviewData = {
      ...review,
      sentiment: normalizedSentiment,
    };
    console.log('Sending review to backend API:', reviewData);
    
    const response = await api.post('/reviews', reviewData);
    console.log('Review created successfully:', response.data);
    
    return response.data;
  } catch (error) {
    console.error('Error in createReview:', error);
    if (axios.isAxiosError(error)) {
      console.error('API Error details:', {
        endpoint: error.config?.url,
        method: error.config?.method,
        statusCode: error.response?.status,
        responseData: error.response?.data,
      });
    }
    throw error;
  }
};

export const getReviews = async (params?: { movieId?: string; page?: number; limit?: number }) => {
  const queryString = new URLSearchParams();
  if (params?.movieId) queryString.append('movieId', params.movieId);
  if (params?.page) queryString.append('page', params.page.toString());
  if (params?.limit) queryString.append('limit', params.limit.toString());
  
  const response = await api.get(`/reviews?${queryString}`);
  return response.data;
};

export const getReview = async (id: string) => {
  const response = await api.get(`/reviews/${id}`);
  return response.data;
};

export const updateReview = async (
  id: string,
  review: {
    movieTitle?: string;
    reviewText?: string;
    rating?: number;
  }
) => {
  const response = await api.put(`/reviews/${id}`, review);
  return response.data;
};

export const deleteReview = async (id: string) => {
  const response = await api.delete(`/reviews/${id}`);
  return response.data;
};

// Stats API
export const getStats = async () => {
  try {
    const response = await api.get('/reviews/stats');
    console.log('Raw stats response:', response);
    return response.data;
  } catch (error) {
    console.error('Error fetching stats:', error);
    // Return default data structure
    return {
      totalReviews: 0,
      averageRating: 0,
      sentimentBreakdown: { positive: 0, negative: 0, neutral: 0 },
      recentReviews: []
    };
  }
};

export const getUserStats = async (userId: string) => {
  try {
    const response = await api.get(`/reviews/stats/user/${userId}`);
    return response.data;
  } catch (error) {
    console.error(`Error fetching user stats for user ${userId}:`, error);
    // Return default data structure
    return {
      totalReviews: 0,
      averageRating: 0,
      sentimentBreakdown: { positive: 0, negative: 0, neutral: 0 },
      recentReviews: [],
      monthlyStats: []
    };
  }
};

// ML API
export const analyzeSentiment = async (text: string) => {
  const response = await axios.post(`${ML_API_URL}/predict`, { text });
  return response.data;
};

// Utility functions
export const isAuthenticated = () => {
  const token = localStorage.getItem('token');
  return !!token;
};

export const logout = () => {
  localStorage.removeItem('token');
};

export default api;
