import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import { analyzeSentiment } from '../services/api';
import { FiFilm, FiBarChart2, FiUsers, FiSend } from 'react-icons/fi';
import IconWrapper from '../components/IconWrapper';

const Home: React.FC = () => {
  const navigate = useNavigate();
  const [reviewText, setReviewText] = useState('');
  const [movieTitle, setMovieTitle] = useState('');
  const [rating, setRating] = useState<number>(5);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [recentReviews] = useState([
    {
      id: 1,
      movieTitle: 'Inception (2025)',
      review: 'An absolutely mind-bending masterpiece that keeps you guessing until the end...',
      sentiment: 95,
      author: 'John D.',
    },
    {
      id: 2,
      movieTitle: 'The Matrix (2025)',
      review: 'Revolutionary visual effects combined with a compelling storyline...',
      sentiment: 88,
      author: 'Sarah M.',
    },
  ]);

  const stats = [
    { icon: <IconWrapper>{FiFilm({ size: 32, className: "text-indigo-600" })}</IconWrapper>, value: '1,234', label: 'Reviews Analyzed' },
    { icon: <IconWrapper>{FiBarChart2({ size: 32, className: "text-indigo-600" })}</IconWrapper>, value: '89%', label: 'Accuracy Rate' },
    { icon: <IconWrapper>{FiUsers({ size: 32, className: "text-indigo-600" })}</IconWrapper>, value: '500+', label: 'Movies Reviewed' },
  ];

  const handleAnalysis = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!reviewText.trim() || !movieTitle.trim()) return;
    
    // Check minimum review length
    if (reviewText.trim().length < 10) {
      setError('Review text must be at least 10 characters long.');
      return;
    }

    setLoading(true);
    setError('');
    try {
      const result = await analyzeSentiment(reviewText);
      console.log('Sentiment analysis result:', result);
      
      // Navigate to the results page with the sentiment analysis data
      navigate('/results', { 
        state: { 
          movieTitle,
          reviewText, 
          rating,
          sentiment: result.sentiment, 
          detailedSentiment: result.detailed_sentiment || result.sentiment,
          confidence: result.confidence 
        } 
      });
    } catch (error) {
      console.error('Error analyzing sentiment:', error);
      setError('Failed to analyze sentiment. Please try again.');
      setLoading(false);
    }
  };

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
      className="max-w-6xl mx-auto px-4 py-12"
    >
      <div className="text-center mb-16">
        <motion.h1
          initial={{ y: -20 }}
          animate={{ y: 0 }}
          className="text-5xl font-bold mb-6 bg-gradient-to-r from-indigo-600 to-purple-600 text-transparent bg-clip-text"
        >
          CritiQ - Movie Review Analysis
        </motion.h1>
        <motion.p
          initial={{ y: 20, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ delay: 0.2 }}
          className="text-xl text-gray-600"
        >
          Discover the sentiment behind movie reviews using advanced AI
        </motion.p>
      </div>

      <motion.div
        initial={{ scale: 0.95, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        transition={{ delay: 0.3 }}
        className="max-w-3xl mx-auto mb-20"
      >
        <form onSubmit={handleAnalysis} className="space-y-6">
          <div className="flex flex-col md:flex-row gap-4 mb-4">
            <div className="flex-1">
              <label htmlFor="movieTitle" className="block text-sm font-medium text-gray-700 mb-1">
                Movie Title
              </label>
              <input
                type="text"
                id="movieTitle"
                value={movieTitle}
                onChange={(e) => setMovieTitle(e.target.value)}
                placeholder="Enter movie title"
                className="w-full p-3 border-2 border-gray-200 rounded-xl focus:ring-2 focus:ring-indigo-500 focus:border-transparent text-lg transition-all duration-200"
                required
              />
            </div>
            <div className="w-full md:w-32">
              <label htmlFor="rating" className="block text-sm font-medium text-gray-700 mb-1">
                Rating
              </label>
              <select
                id="rating"
                value={rating}
                onChange={(e) => setRating(Number(e.target.value))}
                className="w-full p-3 border-2 border-gray-200 rounded-xl focus:ring-2 focus:ring-indigo-500 focus:border-transparent text-lg transition-all duration-200"
              >
                {[1, 2, 3, 4, 5].map((num) => (
                  <option key={num} value={num}>
                    {num} Star{num !== 1 ? 's' : ''}
                  </option>
                ))}
              </select>
            </div>
          </div>
          
          <div className="relative">
            <label htmlFor="reviewText" className="block text-sm font-medium text-gray-700 mb-1">
              Your Review <span className="text-xs text-gray-500">(minimum 10 characters)</span>
            </label>
            <textarea
              id="reviewText"
              value={reviewText}
              onChange={(e) => setReviewText(e.target.value)}
              placeholder="Share your thoughts about the movie..."
              className={`w-full h-40 p-6 border-2 ${
                reviewText.trim().length < 10 && reviewText.trim().length > 0
                  ? 'border-red-300 focus:ring-red-500'
                  : 'border-gray-200 focus:ring-indigo-500'
              } rounded-xl focus:ring-2 focus:border-transparent text-lg transition-all duration-200 ease-in-out shadow-sm hover:shadow-md`}
              required
            />
            <div className="flex justify-between items-center mt-2">
              <div className={`text-xs ${
                reviewText.trim().length < 10 && reviewText.trim().length > 0
                  ? 'text-red-500'
                  : 'text-gray-500'
              }`}>
                Characters: {reviewText.trim().length}/10 minimum
              </div>
              {error && (
                <div className="text-red-500 text-sm">{error}</div>
              )}
            </div>
            <div className="absolute bottom-4 right-4">
              <motion.button
                type="submit"
                disabled={loading || !reviewText.trim() || !movieTitle.trim()}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                className={`px-8 py-3 rounded-full text-white font-medium flex items-center space-x-2 ${
                  loading || !reviewText.trim() || !movieTitle.trim()
                    ? 'bg-gray-400 cursor-not-allowed'
                    : 'bg-gradient-to-r from-indigo-600 to-purple-600 hover:from-indigo-700 hover:to-purple-700 shadow-lg'
                }`}
              >
                <span>{loading ? 'Analyzing...' : 'Analyze'}</span>
                {!loading && <IconWrapper>{FiSend({ size: 20 })}</IconWrapper>}
              </motion.button>
            </div>
          </div>
        </form>
      </motion.div>

      <motion.div
        initial={{ y: 40, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ delay: 0.4 }}
        className="mb-20"
      >
        <h2 className="text-3xl font-bold mb-8 text-center">Recent Reviews</h2>
        <div className="grid md:grid-cols-2 gap-8">
          {recentReviews.map((review, index) => (
            <motion.div
              key={review.id}
              initial={{ opacity: 0, x: index % 2 === 0 ? -20 : 20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ delay: 0.5 + index * 0.2 }}
              className="bg-white rounded-xl p-6 shadow-lg hover:shadow-xl transition-shadow duration-200"
            >
              <div className="flex justify-between items-start mb-4">
                <h3 className="font-bold text-xl text-gray-800">{review.movieTitle}</h3>
                <div className="flex items-center bg-green-100 px-3 py-1 rounded-full">
                  <span className="text-green-600 font-bold">{review.sentiment}%</span>
                </div>
              </div>
              <p className="text-gray-600 mb-6 italic">"{review.review}"</p>
              <div className="flex items-center">
                <div className="w-10 h-10 bg-gradient-to-r from-indigo-500 to-purple-500 rounded-full flex items-center justify-center text-white font-semibold">
                  {review.author[0]}
                </div>
                <span className="ml-3 text-gray-700 font-medium">{review.author}</span>
              </div>
            </motion.div>
          ))}
        </div>
      </motion.div>

      <motion.div
        initial={{ y: 40, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ delay: 0.6 }}
        className="grid md:grid-cols-3 gap-8"
      >
        {stats.map((stat, index) => (
          <motion.div
            key={index}
            whileHover={{ y: -5 }}
            className="bg-white rounded-xl p-8 text-center shadow-lg hover:shadow-xl transition-all duration-200"
          >
            <div className="flex justify-center mb-4">{stat.icon}</div>
            <div className="text-4xl font-bold mb-2 bg-gradient-to-r from-indigo-600 to-purple-600 text-transparent bg-clip-text">
              {stat.value}
            </div>
            <div className="text-gray-600 font-medium">{stat.label}</div>
          </motion.div>
        ))}
      </motion.div>
    </motion.div>
  );
};

export default Home;
