import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { getReviews, getStats } from '../services/api';
import { motion } from 'framer-motion';

interface Review {
  _id: string;
  movieTitle: string;
  reviewText: string;
  rating: number;
  sentiment: string;
  createdAt: string;
}

interface Stats {
  totalReviews: number;
  averageRating: number;
  sentimentDistribution: {
    positive: number;
    negative: number;
    neutral: number;
  };
}

const Dashboard: React.FC = () => {
  const [reviews, setReviews] = useState<Review[]>([]);
  const [stats, setStats] = useState<Stats | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const { user, logout } = useAuth();

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const [reviewsData, statsData] = await Promise.all([
          getReviews(),
          getStats(),
        ]);
        setReviews(reviewsData);
        setStats(statsData);
      } catch (err) {
        setError('Failed to fetch data');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-indigo-500"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-red-500">{error}</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 py-8 px-4 sm:px-6 lg:px-8">
      <div className="max-w-7xl mx-auto">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-3xl font-bold text-gray-900">
            Welcome, {user?.name}!
          </h1>
          <button
            onClick={handleLogout}
            className="px-4 py-2 text-sm font-medium text-white bg-red-600 rounded-md hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500"
          >
            Logout
          </button>
        </div>

        {stats && (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            className="grid grid-cols-1 gap-5 sm:grid-cols-2 lg:grid-cols-3 mb-8"
          >
            <div className="bg-white overflow-hidden shadow rounded-lg">
              <div className="px-4 py-5 sm:p-6">
                <dt className="text-sm font-medium text-gray-500 truncate">
                  Total Reviews
                </dt>
                <dd className="mt-1 text-3xl font-semibold text-gray-900">
                  {stats.totalReviews}
                </dd>
              </div>
            </div>
            <div className="bg-white overflow-hidden shadow rounded-lg">
              <div className="px-4 py-5 sm:p-6">
                <dt className="text-sm font-medium text-gray-500 truncate">
                  Average Rating
                </dt>
                <dd className="mt-1 text-3xl font-semibold text-gray-900">
                  {stats.averageRating.toFixed(1)}
                </dd>
              </div>
            </div>
            <div className="bg-white overflow-hidden shadow rounded-lg">
              <div className="px-4 py-5 sm:p-6">
                <dt className="text-sm font-medium text-gray-500 truncate">
                  Sentiment Distribution
                </dt>
                <dd className="mt-1 text-sm text-gray-900">
                  <div className="space-y-1">
                    <div className="flex justify-between">
                      <span>Positive:</span>
                      <span>{stats.sentimentDistribution.positive}%</span>
                    </div>
                    <div className="flex justify-between">
                      <span>Neutral:</span>
                      <span>{stats.sentimentDistribution.neutral}%</span>
                    </div>
                    <div className="flex justify-between">
                      <span>Negative:</span>
                      <span>{stats.sentimentDistribution.negative}%</span>
                    </div>
                  </div>
                </dd>
              </div>
            </div>
          </motion.div>
        )}

        <div className="bg-white shadow overflow-hidden sm:rounded-md">
          <ul className="divide-y divide-gray-200">
            {reviews.map((review) => (
              <motion.li
                key={review._id}
                initial={{ opacity: 0, x: -20 }}
                animate={{ opacity: 1, x: 0 }}
                whileHover={{ scale: 1.02 }}
                className="px-4 py-4 sm:px-6"
              >
                <div className="flex items-center justify-between">
                  <div className="flex-1 min-w-0">
                    <h3 className="text-lg font-medium text-gray-900 truncate">
                      {review.movieTitle}
                    </h3>
                    <p className="mt-1 text-sm text-gray-500">
                      {review.reviewText}
                    </p>
                    <div className="mt-2 flex items-center">
                      <span className="text-sm text-gray-500">
                        Rating: {review.rating}/5
                      </span>
                      <span
                        className={`ml-2 px-2 py-1 text-xs font-medium rounded-full ${
                          review.sentiment === 'positive'
                            ? 'bg-green-100 text-green-800'
                            : review.sentiment === 'negative'
                            ? 'bg-red-100 text-red-800'
                            : 'bg-gray-100 text-gray-800'
                        }`}
                      >
                        {review.sentiment}
                      </span>
                    </div>
                  </div>
                  <div className="ml-4 flex-shrink-0">
                    <button
                      onClick={() => navigate(`/review/${review._id}`)}
                      className="font-medium text-indigo-600 hover:text-indigo-500"
                    >
                      View Details
                    </button>
                  </div>
                </div>
              </motion.li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
