import { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { getReview, updateReview, deleteReview } from '../services/api';
import { motion } from 'framer-motion';

interface Review {
  _id: string;
  movieTitle: string;
  reviewText: string;
  rating: number;
  sentiment: string;
  createdAt: string;
}

const ReviewPage = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const { isAuthenticated } = useAuth();
  const [review, setReview] = useState<Review | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const [editedReview, setEditedReview] = useState({
    movieTitle: '',
    reviewText: '',
    rating: 0,
  });

  useEffect(() => {
    const fetchReview = async () => {
      try {
        if (!id) return;
        const data = await getReview(id);
        setReview(data);
        setEditedReview({
          movieTitle: data.movieTitle,
          reviewText: data.reviewText,
          rating: data.rating,
        });
      } catch (err) {
        setError('Failed to fetch review');
      } finally {
        setLoading(false);
      }
    };

    fetchReview();
  }, [id]);

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleSave = async () => {
    try {
      if (!id) return;
      const updatedReview = await updateReview(id, editedReview);
      setReview(updatedReview);
      setIsEditing(false);
    } catch (err) {
      setError('Failed to update review');
    }
  };

  const handleDelete = async () => {
    try {
      if (!id) return;
      await deleteReview(id);
      navigate('/dashboard');
    } catch (err) {
      setError('Failed to delete review');
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-red-500">{error}</div>
      </div>
    );
  }

  if (!review) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-gray-500">Review not found</div>
      </div>
    );
  }

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      className="max-w-4xl mx-auto p-6"
    >
      <div className="bg-white rounded-lg shadow-lg p-6">
        {isEditing ? (
          <div className="space-y-4">
            <input
              type="text"
              value={editedReview.movieTitle}
              onChange={(e) =>
                setEditedReview({ ...editedReview, movieTitle: e.target.value })
              }
              className="w-full p-2 border rounded"
              placeholder="Movie Title"
            />
            <textarea
              value={editedReview.reviewText}
              onChange={(e) =>
                setEditedReview({ ...editedReview, reviewText: e.target.value })
              }
              className="w-full p-2 border rounded"
              placeholder="Review Text"
              rows={4}
            />
            <input
              type="number"
              value={editedReview.rating}
              onChange={(e) =>
                setEditedReview({
                  ...editedReview,
                  rating: parseInt(e.target.value),
                })
              }
              className="w-full p-2 border rounded"
              placeholder="Rating"
              min="1"
              max="5"
            />
            <div className="flex space-x-4">
              <button
                onClick={handleSave}
                className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
              >
                Save
              </button>
              <button
                onClick={() => setIsEditing(false)}
                className="px-4 py-2 bg-gray-500 text-white rounded hover:bg-gray-600"
              >
                Cancel
              </button>
            </div>
          </div>
        ) : (
          <div className="space-y-4">
            <h1 className="text-2xl font-bold">{review.movieTitle}</h1>
            <p className="text-gray-600">{review.reviewText}</p>
            <div className="flex items-center space-x-2">
              <span className="text-yellow-500">
                {'★'.repeat(review.rating)}
                {'☆'.repeat(5 - review.rating)}
              </span>
              <span className="text-gray-500">({review.rating}/5)</span>
            </div>
            <div className="flex items-center space-x-2">
              <span className="text-gray-500">Sentiment:</span>
              <span
                className={`px-2 py-1 rounded ${
                  review.sentiment === 'positive'
                    ? 'bg-green-100 text-green-800'
                    : review.sentiment === 'negative'
                    ? 'bg-red-100 text-red-800'
                    : 'bg-yellow-100 text-yellow-800'
                }`}
              >
                {review.sentiment}
              </span>
            </div>
            <div className="text-gray-500">
              Posted on {new Date(review.createdAt).toLocaleDateString()}
            </div>
            {isAuthenticated && (
              <div className="flex space-x-4">
                <button
                  onClick={handleEdit}
                  className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
                >
                  Edit
                </button>
                <button
                  onClick={handleDelete}
                  className="px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600"
                >
                  Delete
                </button>
              </div>
            )}
          </div>
        )}
      </div>
    </motion.div>
  );
};

export default ReviewPage;
