import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';

interface ResultLocationState {
  reviewText: string;
  sentiment: string;
  confidence: number;
}

const ResultPage: React.FC = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { reviewText, sentiment, confidence } = (location.state as ResultLocationState) || {
    reviewText: '',
    sentiment: '',
    confidence: 0,
  };

  // If no review text, redirect back to home
  React.useEffect(() => {
    if (!reviewText) {
      navigate('/');
    }
  }, [reviewText, navigate]);

  const getSentimentColor = () => {
    if (sentiment.includes('positive')) {
      return 'text-green-600';
    } else if (sentiment.includes('negative')) {
      return 'text-red-600';
    } else {
      return 'text-blue-600';
    }
  };

  const getSentimentBgColor = () => {
    if (sentiment.includes('positive')) {
      return 'bg-green-100';
    } else if (sentiment.includes('negative')) {
      return 'bg-red-100';
    } else {
      return 'bg-blue-100';
    }
  };

  const formatConfidence = (confidence: number) => {
    return Math.round(confidence * 100) + '%';
  };

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
      className="max-w-4xl mx-auto px-4 py-20"
    >
      <motion.div
        initial={{ y: 20, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        className="bg-white rounded-xl p-8 shadow-xl"
      >
        <h1 className="text-3xl font-bold mb-6 text-center bg-gradient-to-r from-indigo-600 to-purple-600 text-transparent bg-clip-text">
          Sentiment Analysis Results
        </h1>

        <div className="mb-8">
          <h2 className="text-xl font-semibold mb-3 text-gray-700">Your Review</h2>
          <div className="bg-gray-50 p-6 rounded-lg border border-gray-200 italic text-gray-700">
            "{reviewText}"
          </div>
        </div>

        <div className="grid md:grid-cols-2 gap-8 mb-10">
          <div className="bg-white p-6 rounded-lg border border-gray-200 shadow-sm">
            <h3 className="text-lg font-semibold mb-4 text-gray-700">Sentiment</h3>
            <div className={`text-3xl font-bold ${getSentimentColor()} capitalize`}>
              {sentiment}
            </div>
          </div>

          <div className="bg-white p-6 rounded-lg border border-gray-200 shadow-sm">
            <h3 className="text-lg font-semibold mb-4 text-gray-700">Confidence</h3>
            <div className="flex items-center">
              <div className="flex-1 bg-gray-200 rounded-full h-4">
                <div
                  className={`h-4 rounded-full ${getSentimentBgColor()}`}
                  style={{ width: formatConfidence(confidence) }}
                ></div>
              </div>
              <span className="ml-4 text-lg font-bold">{formatConfidence(confidence)}</span>
            </div>
          </div>
        </div>

        <div className="flex justify-center">
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={() => navigate('/')}
            className="px-8 py-3 rounded-full text-white font-medium bg-gradient-to-r from-indigo-600 to-purple-600 hover:from-indigo-700 hover:to-purple-700 shadow-lg"
          >
            Analyze Another Review
          </motion.button>
        </div>
      </motion.div>
    </motion.div>
  );
};

export default ResultPage;
