import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { FiSearch, FiStar, FiChevronLeft, FiChevronRight } from 'react-icons/fi';
import Icon from '../components/Icon';
import LoadingSpinner from '../components/LoadingSpinner';

interface Movie {
  id: string;
  title: string;
  posterPath: string;
  releaseDate: string;
  voteAverage: number;
  overview: string;
}

const Movies: React.FC = () => {
  const [movies, setMovies] = useState<Movie[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [searchQuery, setSearchQuery] = useState<string>('');
  const [filteredMovies, setFilteredMovies] = useState<Movie[]>([]);

  // For pagination
  const [currentPage, setCurrentPage] = useState<number>(1);
  const moviesPerPage = 12;

  useEffect(() => {
    const fetchMovies = async () => {
      try {
        setLoading(true);
        // In a real app, we would fetch from TMDB API
        // const response = await axios.get('https://api.themoviedb.org/3/movie/popular', {
        //   params: { api_key: process.env.REACT_APP_TMDB_API_KEY }
        // });
        
        // Mock data
        const mockMovies = [
          {
            id: '1',
            title: 'Inception',
            posterPath: 'https://image.tmdb.org/t/p/w500/9gk7adHYeDvHkCSEqAvQNLV5Uge.jpg',
            releaseDate: '2010-07-16',
            voteAverage: 8.4,
            overview: 'A thief who steals corporate secrets through the use of dream-sharing technology is given the inverse task of planting an idea into the mind of a C.E.O.'
          },
          {
            id: '2',
            title: 'Interstellar',
            posterPath: 'https://image.tmdb.org/t/p/w500/gEU2QniE6E77NI6lCU6MxlNBvIx.jpg',
            releaseDate: '2014-11-05',
            voteAverage: 8.3,
            overview: 'A team of explorers travel through a wormhole in space in an attempt to ensure humanity\'s survival.'
          },
          {
            id: '3',
            title: 'The Dark Knight',
            posterPath: 'https://image.tmdb.org/t/p/w500/qJ2tW6WMUDux911r6m7haRef0WH.jpg',
            releaseDate: '2008-07-18',
            voteAverage: 8.5,
            overview: 'When the menace known as The Joker emerges from his mysterious past, he wreaks havoc and chaos on the people of Gotham.'
          },
          {
            id: '4',
            title: 'Pulp Fiction',
            posterPath: 'https://image.tmdb.org/t/p/w500/d5iIlFn5s0ImszYzBPb8JPIfbXD.jpg',
            releaseDate: '1994-10-14',
            voteAverage: 8.5,
            overview: 'The lives of two mob hitmen, a boxer, a gangster and his wife, and a pair of diner bandits intertwine in four tales of violence and redemption.'
          },
          {
            id: '5',
            title: 'The Godfather',
            posterPath: 'https://image.tmdb.org/t/p/w500/3bhkrj58Vtu7enYsRolD1fZdja1.jpg',
            releaseDate: '1972-03-14',
            voteAverage: 8.7,
            overview: 'The aging patriarch of an organized crime dynasty transfers control of his clandestine empire to his reluctant son.'
          },
          {
            id: '6',
            title: 'The Shawshank Redemption',
            posterPath: 'https://image.tmdb.org/t/p/w500/q6y0Go1tsGEsmtFryDOJo3dEmqu.jpg',
            releaseDate: '1994-09-23',
            voteAverage: 8.7,
            overview: 'Two imprisoned men bond over a number of years, finding solace and eventual redemption through acts of common decency.'
          },
          {
            id: '7',
            title: 'The Matrix',
            posterPath: 'https://image.tmdb.org/t/p/w500/f89U3ADr1oiB1s9GkdPOEpXUk5H.jpg',
            releaseDate: '1999-03-30',
            voteAverage: 8.1,
            overview: 'A computer hacker learns from mysterious rebels about the true nature of his reality and his role in the war against its controllers.'
          },
          {
            id: '8',
            title: 'Fight Club',
            posterPath: 'https://image.tmdb.org/t/p/w500/8kNruSfhk5IoE4eZOc4UpvDn6tq.jpg',
            releaseDate: '1999-10-15',
            voteAverage: 8.4,
            overview: 'An insomniac office worker and a devil-may-care soapmaker form an underground fight club that evolves into something much, much more.'
          },
          {
            id: '9',
            title: 'Goodfellas',
            posterPath: 'https://image.tmdb.org/t/p/w500/aKuFiU82s5ISJpGZp7YkIr3kCUd.jpg',
            releaseDate: '1990-09-12',
            voteAverage: 8.5,
            overview: 'The story of Henry Hill and his life in the mob, covering his relationship with his wife Karen Hill and his mob partners Jimmy Conway and Tommy DeVito.'
          },
          {
            id: '10',
            title: 'Parasite',
            posterPath: 'https://image.tmdb.org/t/p/w500/7IiTTgloJzvGI1TAYymCfbfl3vT.jpg',
            releaseDate: '2019-05-30',
            voteAverage: 8.5,
            overview: 'All unemployed, Ki-taek\'s family takes peculiar interest in the wealthy and glamorous Parks for their livelihood until they get entangled in an unexpected incident.'
          },
          {
            id: '11',
            title: 'The Lord of the Rings: The Fellowship of the Ring',
            posterPath: 'https://image.tmdb.org/t/p/w500/6oom5QYQ2yQTMJIbnvbkBL9cHo6.jpg',
            releaseDate: '2001-12-18',
            voteAverage: 8.3,
            overview: 'A meek Hobbit from the Shire and eight companions set out on a journey to destroy the powerful One Ring and save Middle-earth from the Dark Lord Sauron.'
          },
          {
            id: '12',
            title: 'Forrest Gump',
            posterPath: 'https://image.tmdb.org/t/p/w500/saHP97rTPS5eLmrLQEcANmKrsFl.jpg',
            releaseDate: '1994-06-23',
            voteAverage: 8.5,
            overview: 'The presidencies of Kennedy and Johnson, the events of Vietnam, Watergate, and other history unfold through the perspective of an Alabama man with an IQ of 75.'
          },
          {
            id: '13',
            title: 'The Silence of the Lambs',
            posterPath: 'https://image.tmdb.org/t/p/w500/rplLJ2hPcOQmkFhTqUte0MkEaO2.jpg',
            releaseDate: '1991-02-01',
            voteAverage: 8.3,
            overview: 'A young F.B.I. cadet must receive the help of an incarcerated and manipulative cannibal killer to help catch another serial killer.'
          },
          {
            id: '14',
            title: 'Schindler\'s List',
            posterPath: 'https://image.tmdb.org/t/p/w500/sF1U4EUQS8YHUYjNl3pMGNIQyr0.jpg',
            releaseDate: '1993-12-15',
            voteAverage: 8.6,
            overview: 'In German-occupied Poland during World War II, industrialist Oskar Schindler gradually becomes concerned for his Jewish workforce after witnessing their persecution by the Nazis.'
          },
          {
            id: '15',
            title: 'Spirited Away',
            posterPath: 'https://image.tmdb.org/t/p/w500/39wmItIWsg5sZMyRUHLkWBcuVCM.jpg',
            releaseDate: '2001-07-20',
            voteAverage: 8.5,
            overview: 'During her family\'s move to the suburbs, a sullen 10-year-old girl wanders into a world ruled by gods, witches, and spirits, and where humans are changed into beasts.'
          },
        ];
        
        setMovies(mockMovies);
        setFilteredMovies(mockMovies);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching movies:', error);
        setLoading(false);
      }
    };
    
    fetchMovies();
  }, []);

  useEffect(() => {
    if (searchQuery.trim() === '') {
      setFilteredMovies(movies);
    } else {
      const filtered = movies.filter(movie => 
        movie.title.toLowerCase().includes(searchQuery.toLowerCase())
      );
      setFilteredMovies(filtered);
    }
    setCurrentPage(1);
  }, [searchQuery, movies]);

  // Calculate pagination
  const indexOfLastMovie = currentPage * moviesPerPage;
  const indexOfFirstMovie = indexOfLastMovie - moviesPerPage;
  const currentMovies = filteredMovies.slice(indexOfFirstMovie, indexOfLastMovie);
  const totalPages = Math.ceil(filteredMovies.length / moviesPerPage);

  const handlePageChange = (pageNumber: number) => {
    setCurrentPage(pageNumber);
    window.scrollTo(0, 0);
  };

  return (
    <div className="container mx-auto py-8 px-4">
      <h1 className="text-3xl font-bold mb-8 text-gray-800">Discover Movies</h1>
      
      {/* Search */}
      <div className="mb-8 relative">
        <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
          <Icon icon={FiSearch} size={20} className="text-gray-400" />
        </div>
        <input
          type="text"
          className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
          placeholder="Search movies..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
      </div>
      
      {loading ? (
        <div className="flex justify-center items-center h-64">
          <LoadingSpinner />
        </div>
      ) : (
        <>
          {filteredMovies.length === 0 ? (
            <div className="text-center py-12">
              <p className="text-xl text-gray-600">No movies found matching your search.</p>
            </div>
          ) : (
            <>
              {/* Movies Grid */}
              <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-8">
                {currentMovies.map(movie => (
                  <Link
                    key={movie.id}
                    to={`/movies/${movie.id}`}
                    className="bg-white rounded-xl shadow-md overflow-hidden hover:shadow-lg transition transform hover:-translate-y-1"
                  >
                    <div className="relative h-64">
                      <img
                        src={movie.posterPath}
                        alt={movie.title}
                        className="w-full h-full object-cover"
                      />
                      <div className="absolute top-2 right-2 bg-blue-600 text-white rounded-full px-2 py-1 text-sm flex items-center">
                        <Icon icon={FiStar} size={16} className="mr-1" />
                        {movie.voteAverage}
                      </div>
                    </div>
                    <div className="p-4">
                      <h2 className="font-bold text-lg mb-1 line-clamp-1">{movie.title}</h2>
                      <p className="text-gray-500 text-sm mb-2">{new Date(movie.releaseDate).getFullYear()}</p>
                      <p className="text-gray-700 text-sm line-clamp-3">{movie.overview}</p>
                    </div>
                  </Link>
                ))}
              </div>
              
              {/* Pagination */}
              {totalPages > 1 && (
                <div className="flex justify-center mt-10">
                  <div className="flex space-x-1">
                    <button
                      onClick={() => handlePageChange(currentPage - 1)}
                      disabled={currentPage === 1}
                      className={`px-3 py-2 rounded-md ${
                        currentPage === 1
                          ? 'text-gray-400 cursor-not-allowed'
                          : 'text-blue-600 hover:bg-blue-100'
                      }`}
                    >
                      <Icon icon={FiChevronLeft} size={20} />
                    </button>
                    
                    {[...Array(totalPages)].map((_, i) => (
                      <button
                        key={i}
                        onClick={() => handlePageChange(i + 1)}
                        className={`px-4 py-2 rounded-md ${
                          currentPage === i + 1
                            ? 'bg-blue-600 text-white'
                            : 'text-gray-700 hover:bg-blue-100'
                        }`}
                      >
                        {i + 1}
                      </button>
                    ))}
                    
                    <button
                      onClick={() => handlePageChange(currentPage + 1)}
                      disabled={currentPage === totalPages}
                      className={`px-3 py-2 rounded-md ${
                        currentPage === totalPages
                          ? 'text-gray-400 cursor-not-allowed'
                          : 'text-blue-600 hover:bg-blue-100'
                      }`}
                    >
                      <Icon icon={FiChevronRight} size={20} />
                    </button>
                  </div>
                </div>
              )}
            </>
          )}
        </>
      )}
    </div>
  );
};

export default Movies;
