import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { createReview } from '../services/api';
import { useAuth } from '../context/AuthContext';

interface ReviewFormProps {
  movieId?: string;
  movieTitle?: string;
  onSuccess?: () => void;
}

const ReviewForm: React.FC<ReviewFormProps> = ({ movieId, movieTitle, onSuccess }) => {
  const [formData, setFormData] = useState({
    movieId: movieId || '',
    movieTitle: movieTitle || '',
    reviewText: '',
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [debugMessages, setDebugMessages] = useState<string[]>([]);
  const navigate = useNavigate();
  const { isAuthenticated, user, token } = useAuth();

  // Add debug info for troubleshooting
  useEffect(() => {
    if (!isAuthenticated) {
      setDebugMessages(prev => [...prev, 'Not authenticated according to useAuth()']);
    } else {
      setDebugMessages(prev => [...prev, `Authenticated as: ${user?.name || 'unknown'}`]);
    }
    
    if (!token) {
      setDebugMessages(prev => [...prev, 'No token found in Auth context']);
    } else {
      setDebugMessages(prev => [...prev, `Token exists, length: ${token.length}`]);
    }
    
    const localToken = localStorage.getItem('token');
    if (!localToken) {
      setDebugMessages(prev => [...prev, 'No token in localStorage']);
    } else {
      setDebugMessages(prev => [...prev, `Token in localStorage, length: ${localToken.length}`]);
    }
  }, [isAuthenticated, user, token]);

  useEffect(() => {
    if (movieId || movieTitle) {
      setFormData(prev => ({
        ...prev,
        movieId: movieId || prev.movieId,
        movieTitle: movieTitle || prev.movieTitle
      }));
    }
  }, [movieId, movieTitle]);

  const handleChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>
  ) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    if (error) setError('');
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (formData.reviewText.trim().length < 10) {
      setError('Review must be at least 10 characters long');
      return;
    }

    if (formData.movieTitle.trim().length < 1) {
      setError('Movie title is required');
      return;
    }

    // If no movieId provided, use a placeholder
    if (!formData.movieId || formData.movieId.trim() === '') {
      setFormData(prev => ({
        ...prev,
        movieId: 'placeholder-id'
      }));
    }

    if (!isAuthenticated) {
      setDebugMessages(prev => [...prev, 'Redirecting to login due to not being authenticated']);
      navigate('/login', { state: { from: window.location.pathname } });
      return;
    }

    setLoading(true);
    setError('');
    setDebugMessages(prev => [...prev, 'Submitting review...']);

    try {
      // The rating will now be generated by sentiment analysis
      const result = await createReview({
        ...formData,
        // Rating field removed as it will be generated by ML model
      });
      
      setDebugMessages(prev => [...prev, `Review created with ID: ${result._id || 'unknown'}`]);
      setFormData({ movieId: movieId || '', movieTitle: movieTitle || '', reviewText: '' });
      if (onSuccess) {
        onSuccess();
      }
    } catch (err) {
      const errorMessage = (err as Error).message || 'Failed to submit review';
      setError(errorMessage);
      setDebugMessages(prev => [...prev, `Error: ${errorMessage}`]);
      
      // If authentication error, redirect to login
      if (errorMessage.includes('logged in') || errorMessage.includes('session expired')) {
        setTimeout(() => {
          navigate('/login', { state: { from: window.location.pathname } });
        }, 2000);
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-white shadow rounded-lg p-6">
      <h2 className="text-xl font-semibold mb-4">Write a Review</h2>
      <p className="text-gray-600 mb-4">
        Share your thoughts about this movie. Our AI will automatically determine a rating based on your review.
      </p>
      
      {!isAuthenticated && (
        <div className="mb-4 p-2 bg-yellow-100 text-yellow-800 rounded">
          You need to be logged in to submit a review. 
          <button 
            onClick={() => navigate('/login', { state: { from: window.location.pathname } })} 
            className="ml-2 text-blue-600 hover:underline"
          >
            Log in now
          </button>
        </div>
      )}
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label htmlFor="movieTitle" className="block text-sm font-medium text-gray-700">
            Movie Title
          </label>
          <input
            type="text"
            id="movieTitle"
            name="movieTitle"
            required
            value={formData.movieTitle}
            onChange={handleChange}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
            placeholder="Enter movie title"
          />
        </div>

        <div>
          <label htmlFor="reviewText" className="block text-sm font-medium text-gray-700">
            Your Review
          </label>
          <textarea
            id="reviewText"
            name="reviewText"
            rows={6}
            required
            value={formData.reviewText}
            onChange={handleChange}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
            placeholder="Share your thoughts about this movie... (Min. 10 characters)"
          />
        </div>

        {error && (
          <div className="text-red-500 text-sm bg-red-50 p-2 rounded">
            {error}
          </div>
        )}

        <div className="flex justify-end">
          <button
            type="submit"
            disabled={loading || !isAuthenticated}
            className={`inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white ${
              loading || !isAuthenticated
                ? 'bg-indigo-400 cursor-not-allowed'
                : 'bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500'
            }`}
          >
            {loading ? 'Submitting...' : 'Submit Review'}
          </button>
        </div>
      </form>
      
      {process.env.NODE_ENV === 'development' && debugMessages.length > 0 && (
        <div className="mt-4 p-2 bg-gray-100 rounded text-xs">
          <p className="font-bold mb-1">Debug info:</p>
          <ul className="list-disc pl-5">
            {debugMessages.map((msg, i) => (
              <li key={i}>{msg}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default ReviewForm;
