import React, { useState, useEffect } from 'react';
import { FiFilter, FiStar, FiChevronLeft, FiChevronRight } from '../components/icons';
import { Link } from 'react-router-dom';
import axios from 'axios';
import LoadingSpinner from '../components/LoadingSpinner';
import IconWrapper from '../components/IconWrapper';

interface Review {
  _id: string;
  movieTitle: string;
  rating: number;
  reviewText: string;
  sentiment: number;
  user: {
    _id: string;
    name: string;
  };
  createdAt: string;
}

const Reviews: React.FC = () => {
  const [reviews, setReviews] = useState<Review[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [currentPage, setCurrentPage] = useState<number>(1);
  const [totalPages, setTotalPages] = useState<number>(1);
  const [filterRating, setFilterRating] = useState<number | null>(null);
  const [filterSentiment, setFilterSentiment] = useState<string>('all');
  const [sortBy, setSortBy] = useState<string>('newest');

  useEffect(() => {
    const fetchReviews = async () => {
      try {
        setLoading(true);
        
        // Build query parameters
        const queryParams = new URLSearchParams();
        queryParams.append('page', currentPage.toString());
        queryParams.append('limit', '8');
        
        if (filterRating !== null) {
          queryParams.append('rating', filterRating.toString());
        }
        
        if (filterSentiment !== 'all') {
          queryParams.append('sentiment', filterSentiment);
        }
        
        if (sortBy === 'newest') {
          queryParams.append('sort', '-createdAt');
        } else if (sortBy === 'oldest') {
          queryParams.append('sort', 'createdAt');
        } else if (sortBy === 'highest') {
          queryParams.append('sort', '-rating');
        } else if (sortBy === 'lowest') {
          queryParams.append('sort', 'rating');
        }

        // For now, use mock data since the API might not be fully operational
        // In production, you would use:
        // const response = await axios.get(`/api/reviews?${queryParams}`);
        
        // Mock data
        const mockReviews = [
          {
            _id: '1',
            movieTitle: 'Inception',
            rating: 5,
            reviewText: 'A mind-bending journey that makes you question reality. The visual effects are groundbreaking and the story is complex yet engaging.',
            sentiment: 92,
            user: {
              _id: 'user1',
              name: 'John Smith'
            },
            createdAt: '2023-04-12T10:30:00Z'
          },
          {
            _id: '2',
            movieTitle: 'The Dark Knight',
            rating: 5,
            reviewText: 'Heath Ledger\'s Joker performance is legendary. This film transcends the superhero genre and stands as one of the greatest films ever made.',
            sentiment: 95,
            user: {
              _id: 'user2',
              name: 'Sarah Johnson'
            },
            createdAt: '2023-04-10T14:20:00Z'
          },
          {
            _id: '3',
