import React, { useState } from 'react';
import { motion } from 'framer-motion';
import axios from 'axios';

interface SentimentResponse {
  sentiment: number;
  analysis: string;
  is_positive: boolean;
}

const Home: React.FC = () => {
  const [review, setReview] = useState('');
  const [sentiment, setSentiment] = useState<SentimentResponse | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      const response = await axios.post<SentimentResponse>('http://localhost:6001/predict', {
        text: review
      });
      setSentiment(response.data);
    } catch (error) {
      console.error('Error analyzing review:', error);
      setError('Failed to analyze the review. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const getSentimentEmoji = (sentiment: SentimentResponse) => {
    if (sentiment.sentiment > 0.8) return '😄';
    if (sentiment.sentiment > 0.6) return '🙂';
    if (sentiment.sentiment > 0.4) return '😐';
    if (sentiment.sentiment > 0.2) return '🙁';
    return '😞';
  };

  const getSentimentColor = (sentiment: SentimentResponse) => {
    if (sentiment.sentiment > 0.8) return 'text-green-600';
    if (sentiment.sentiment > 0.6) return 'text-green-500';
    if (sentiment.sentiment > 0.4) return 'text-yellow-500';
    if (sentiment.sentiment > 0.2) return 'text-orange-500';
    return 'text-red-500';
  };

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, y: -20 }}
      className="max-w-4xl mx-auto px-4 py-8"
    >
      <h1 className="text-3xl font-bold text-center mb-8">
        Movie Review Sentiment Analysis
      </h1>
      <p className="text-gray-600 text-center mb-8">
        Analyze the sentiment of movie reviews using AI
      </p>

      <div className="bg-white rounded-lg shadow-md p-6">
        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="mb-4">
            <textarea
              value={review}
              onChange={(e) => setReview(e.target.value)}
              className="w-full h-32 p-4 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-none"
              placeholder="Enter your movie review here..."
              required
              minLength={10}
            />
          </div>
          <div className="flex justify-center">
            <motion.button
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
              type="submit"
              className="bg-blue-600 text-white px-8 py-3 rounded-md font-medium hover:bg-blue-700 disabled:opacity-50 disabled:hover:bg-blue-600 transition-colors"
              disabled={loading || !review.trim()}
            >
              {loading ? (
                <span className="flex items-center">
                  <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                  </svg>
                  Analyzing...
                </span>
              ) : (
                'Analyze Review'
              )}
            </motion.button>
          </div>
        </form>

        {error && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            className="mt-4 p-4 bg-red-50 text-red-700 rounded-md text-center"
          >
            {error}
          </motion.div>
        )}

        {sentiment && !error && (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            className="mt-8 space-y-4"
          >
            <div className="text-center">
              <div className="text-6xl mb-4">
                {getSentimentEmoji(sentiment)}
              </div>
              <div className={`text-2xl font-bold mb-2 ${getSentimentColor(sentiment)}`}>
                {Math.round(sentiment.sentiment * 100)}% {sentiment.is_positive ? 'Positive' : 'Negative'}
              </div>
              <p className="text-gray-600">{sentiment.analysis}</p>
            </div>
          </motion.div>
        )}
      </div>
    </motion.div>
  );
};

export default Home;
