import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { analyzeSentiment } from '../services/api';

const Home: React.FC = () => {
  const [reviewText, setReviewText] = useState('');
  const [loading, setLoading] = useState(false);
  const [recentReviews] = useState([
    {
      id: 1,
      movieTitle: 'Inception (2025)',
      review: 'An absolutely mind-bending masterpiece that keeps you guessing until the end...',
      sentiment: 95,
      author: 'John D.'
    },
    {
      id: 2,
      movieTitle: 'The Matrix (2025)',
      review: 'Revolutionary visual effects combined with a compelling storyline...',
      sentiment: 88,
      author: 'Sarah M.'
    }
  ]);

  const handleAnalysis = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!reviewText.trim()) return;

    setLoading(true);
    try {
      const result = await analyzeSentiment(reviewText);
      console.log('Sentiment analysis result:', result);
      // Handle the result as needed
    } catch (error) {
      console.error('Error analyzing sentiment:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-6xl mx-auto px-4 py-8">
      <div className="text-center mb-12">
        <h1 className="text-4xl font-bold mb-4">CritiQ - Movie Review Analysis</h1>
        <p className="text-gray-600">Analyze the sentiment of movie reviews using AI</p>
      </div>

      {/* Analysis Input Section */}
      <div className="max-w-3xl mx-auto mb-16">
        <form onSubmit={handleAnalysis} className="space-y-4">
          <textarea
            value={reviewText}
            onChange={(e) => setReviewText(e.target.value)}
            placeholder="Enter your movie review here..."
            className="w-full h-32 p-4 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
          />
          <div className="text-center">
            <button
              type="submit"
              disabled={loading || !reviewText.trim()}
              className={`px-8 py-3 rounded-full text-white font-medium ${
                loading || !reviewText.trim()
                  ? 'bg-gray-400 cursor-not-allowed'
                  : 'bg-black hover:bg-gray-800'
              }`}
            >
              {loading ? 'Analyzing...' : 'Analysis'}
            </button>
          </div>
        </form>
      </div>

      {/* Recent Reviews Section */}
      <div className="grid md:grid-cols-2 gap-8 mb-16">
        {recentReviews.map((review) => (
          <motion.div
            key={review.id}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            className="bg-white rounded-lg p-6 shadow-sm"
          >
            <div className="flex justify-between items-start mb-4">
              <h3 className="font-semibold text-lg">{review.movieTitle}</h3>
              <div className="flex items-center">
                <span className="text-green-500 font-semibold">{review.sentiment}%</span>
              </div>
            </div>
            <p className="text-gray-600 mb-4">"{review.review}"</p>
            <div className="flex items-center">
              <div className="w-8 h-8 bg-gray-200 rounded-full flex items-center justify-center">
                {review.author[0]}
              </div>
              <span className="ml-2 text-gray-600">{review.author}</span>
            </div>
          </motion.div>
        ))}
      </div>

      {/* Statistics Section */}
      <div className="grid grid-cols-3 gap-8">
        <div className="bg-white rounded-lg p-6 text-center shadow-sm">
          <div className="text-4xl font-bold mb-2">1,234</div>
          <div className="text-gray-600">Reviews Analyzed</div>
        </div>
        <div className="bg-white rounded-lg p-6 text-center shadow-sm">
          <div className="text-4xl font-bold mb-2">89%</div>
          <div className="text-gray-600">Accuracy Rate</div>
        </div>
        <div className="bg-white rounded-lg p-6 text-center shadow-sm">
          <div className="text-4xl font-bold mb-2">500+</div>
          <div className="text-gray-600">Movies Reviewed</div>
        </div>
      </div>
    </div>
  );
};

export default Home;
