import React, { useState, useEffect } from 'react';
import { FiSearch, FiStar } from 'react-icons/fi';
import axios from 'axios';
import { Link } from 'react-router-dom';
import LoadingSpinner from '../components/LoadingSpinner';

interface Movie {
  id: string;
  title: string;
  poster_path: string;
  release_date: string;
  vote_average: number;
  overview: string;
}

const Movies: React.FC = () => {
  const [movies, setMovies] = useState<Movie[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [searchQuery, setSearchQuery] = useState<string>('');
  const [filteredMovies, setFilteredMovies] = useState<Movie[]>([]);

  useEffect(() => {
    const fetchMovies = async () => {
      try {
        // This would typically come from your own API, but we're using TMDB for demonstration
        const API_KEY = 'placeholder-key'; // Replace with actual API key if using TMDB directly
        const response = await axios.get(
          `https://api.themoviedb.org/3/movie/popular?api_key=${API_KEY}&language=en-US&page=1`
        );
        
        // For development, we'll use mock data
        const mockMovies = [
          {
            id: '1',
            title: 'Inception',
            poster_path: 'https://image.tmdb.org/t/p/w500/9gk7adHYeDvHkCSEqAvQNLV5Uge.jpg',
            release_date: '2010-07-16',
            vote_average: 8.4,
            overview: 'A thief who steals corporate secrets through the use of dream-sharing technology is given the inverse task of planting an idea into the mind of a C.E.O.'
          },
          {
            id: '2',
            title: 'The Shawshank Redemption',
            poster_path: 'https://image.tmdb.org/t/p/w500/q6y0Go1tsGEsmtFryDOJo3dEmqu.jpg',
            release_date: '1994-09-23',
            vote_average: 8.7,
            overview: 'Framed in the 1940s for the double murder of his wife and her lover, upstanding banker Andy Dufresne begins a new life at the Shawshank prison.'
          },
          {
            id: '3',
            title: 'The Dark Knight',
            poster_path: 'https://image.tmdb.org/t/p/w500/qJ2tW6WMUDux911r6m7haRef0WH.jpg',
            release_date: '2008-07-18',
            vote_average: 8.5,
            overview: 'When the menace known as the Joker wreaks havoc and chaos on the people of Gotham, Batman must accept one of the greatest psychological and physical tests of his ability to fight injustice.'
          },
          {
            id: '4',
            title: 'Pulp Fiction',
            poster_path: 'https://image.tmdb.org/t/p/w500/d5iIlFn5s0ImszYzBPb8JPIfbXD.jpg',
            release_date: '1994-10-14',
            vote_average: 8.5,
            overview: 'The lives of two mob hitmen, a boxer, a gangster and his wife, and a pair of diner bandits intertwine in four tales of violence and redemption.'
          },
          {
            id: '5',
            title: 'The Godfather',
            poster_path: 'https://image.tmdb.org/t/p/w500/3bhkrj58Vtu7enYsRolD1fZdja1.jpg',
            release_date: '1972-03-15',
            vote_average: 8.7,
            overview: 'The aging patriarch of an organized crime dynasty transfers control of his clandestine empire to his reluctant son.'
          },
          {
            id: '6',
            title: 'Fight Club',
            poster_path: 'https://image.tmdb.org/t/p/w500/pB8BM7pdSp6B6Ih7QZ4DrQ3PmJK.jpg',
            release_date: '1999-10-15',
            vote_average: 8.4,
            overview: 'An insomniac office worker and a devil-may-care soapmaker form an underground fight club that evolves into something much, much more.'
          },
          {
            id: '7',
            title: 'Forrest Gump',
            poster_path: 'https://image.tmdb.org/t/p/w500/saHP97rTPS5eLmrLQEcANmKrsFl.jpg',
            release_date: '1994-07-06',
            vote_average: 8.5,
            overview: 'The presidencies of Kennedy and Johnson, the events of Vietnam, Watergate, and other history unfold through the perspective of an Alabama man with an IQ of 75.'
          },
          {
            id: '8',
            title: 'The Matrix',
            poster_path: 'https://image.tmdb.org/t/p/w500/f89U3ADr1oiB1s9GkdPOEpXUk5H.jpg',
            release_date: '1999-03-31',
            vote_average: 8.2,
            overview: 'A computer hacker learns from mysterious rebels about the true nature of his reality and his role in the war against its controllers.'
          }
        ];
        
        setMovies(mockMovies);
        setFilteredMovies(mockMovies);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching movies:', error);
        setLoading(false);
      }
    };

    fetchMovies();
  }, []);

  useEffect(() => {
    if (searchQuery) {
      const filtered = movies.filter(movie => 
        movie.title.toLowerCase().includes(searchQuery.toLowerCase())
      );
      setFilteredMovies(filtered);
    } else {
      setFilteredMovies(movies);
    }
  }, [searchQuery, movies]);

  const handleSearch = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSearchQuery(e.target.value);
  };

  if (loading) {
    return <LoadingSpinner />;
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold text-gray-800 mb-8">Popular Movies</h1>
      
      <div className="relative w-full max-w-md mb-8 mx-auto">
        <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
          <FiSearch className="text-gray-400" />
        </div>
        <input
          type="text"
          className="block w-full pl-10 pr-3 py-2 border border-gray-300 rounded-md leading-5 bg-white placeholder-gray-500 focus:outline-none focus:ring-blue-500 focus:border-blue-500"
          placeholder="Search movies..."
          value={searchQuery}
          onChange={handleSearch}
        />
      </div>
      
      {filteredMovies.length === 0 ? (
        <div className="text-center text-gray-500 py-10">
          No movies found matching your search.
        </div>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
          {filteredMovies.map((movie) => (
            <Link 
              key={movie.id} 
              to={`/movies/${movie.id}`}
              className="bg-white rounded-lg shadow-md overflow-hidden transition transform hover:scale-105 hover:shadow-xl"
            >
              <div className="aspect-w-2 aspect-h-3 relative">
                <img
                  src={movie.poster_path}
                  alt={movie.title}
                  className="object-cover w-full h-full"
                />
                <div className="absolute top-2 right-2 bg-blue-600 text-white rounded-full px-2 py-1 text-sm flex items-center">
                  <FiStar className="mr-1" />
                  {movie.vote_average}
                </div>
              </div>
              <div className="p-4">
                <h3 className="font-semibold text-lg text-gray-800 mb-1 truncate">{movie.title}</h3>
                <p className="text-gray-500 text-sm">{new Date(movie.release_date).getFullYear()}</p>
                <p className="text-gray-600 text-sm mt-2 line-clamp-2">{movie.overview}</p>
              </div>
            </Link>
          ))}
        </div>
      )}
    </div>
  );
};

export default Movies;
