import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { FiStar, FiClock, FiMessageSquare, FiEdit, FiArrowLeft } from 'react-icons/fi';
import LoadingSpinner from '../components/LoadingSpinner';
import Icon from '../components/Icon';
import { movieDatabase } from '../data/movieDatabase';

interface Movie {
  id: string;
  title: string;
  poster_path: string;
  backdrop_path: string;
  release_date: string;
  vote_average: number;
  genres: string[];
  runtime: number;
  overview: string;
}

interface Review {
  _id: string;
  user: {
    _id: string;
    name: string;
  };
  rating: number;
  reviewText: string;
  sentiment: number;
  createdAt: string;
}

const MovieDetail: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const [movie, setMovie] = useState<Movie | null>(null);
  const [reviews, setReviews] = useState<Review[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchMovieData = async () => {
      try {
        setLoading(true);

        // Find the movie from our database using the ID parameter
        const foundMovie = movieDatabase.find(movie => movie.id === id);

        if (foundMovie) {
          // Convert our Movie type to the expected Movie interface
          const movieData: Movie = {
            id: foundMovie.id,
            title: foundMovie.title,
            poster_path: foundMovie.image,
            backdrop_path: foundMovie.image, // We could use a different backdrop image if available
            release_date: `${foundMovie.year}-01-01`,
            vote_average: foundMovie.rating,
            genres: foundMovie.genre || ['Drama'],
            runtime: 120, // Default runtime
            overview: foundMovie.description || 'No description available.'
          };

          setMovie(movieData);
        } else {
          // If movie not found, set movie to null
          console.error(`Movie with ID ${id} not found in database`);
          setMovie(null);
        }

        // Mock reviews - would normally fetch these from API
        const mockReviews: Review[] = [
          {
            _id: '101',
            user: {
              _id: 'user1',
              name: 'John Smith'
            },
            rating: 5,
            reviewText: 'A mind-bending journey that makes you question reality. The visual effects are groundbreaking and the story is complex yet engaging.',
            sentiment: 92,
            createdAt: '2023-04-12T10:30:00Z'
          },
          {
            _id: '102',
            user: {
              _id: 'user2',
              name: 'Sarah Johnson'
            },
            rating: 4,
            reviewText: 'Great concept and execution, though the ending left me with questions. The performances, especially DiCaprio\'s, were excellent.',
            sentiment: 85,
            createdAt: '2023-03-15T14:20:00Z'
          },
          {
            _id: '103',
            user: {
              _id: 'user3',
              name: 'Michael Brown'
            },
            rating: 5,
            reviewText: 'One of Nolan\'s best works. The layered narrative structure and mind-bending concept had me on the edge of my seat throughout.',
            sentiment: 95,
            createdAt: '2023-02-28T09:15:00Z'
          }
        ];

        setReviews(mockReviews);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching movie data:', error);
        setLoading(false);
      }
    };

    fetchMovieData();
  }, [id]);

  const formatDate = (dateString: string) => {
    const options: Intl.DateTimeFormatOptions = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString('en-US', options);
  };

  const formatRuntime = (minutes: number) => {
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;
    return `${hours}h ${mins}m`;
  };

  const getSentimentClass = (sentiment: number) => {
    if (sentiment >= 80) return 'bg-green-100 text-green-800';
    if (sentiment >= 60) return 'bg-blue-100 text-blue-800';
    if (sentiment >= 40) return 'bg-yellow-100 text-yellow-800';
    return 'bg-red-100 text-red-800';
  };

  if (loading) {
    return <LoadingSpinner />;
  }

  if (!movie) {
    return (
      <div className="container mx-auto px-4 py-24 text-center">
        <h1 className="text-3xl font-bold text-gray-800 mb-4">Movie Not Found</h1>
        <p className="text-gray-600 mb-8">The movie you're looking for doesn't exist or has been removed.</p>
        <Link to="/movies" className="text-blue-600 hover:underline flex items-center justify-center">
          <Icon icon={FiArrowLeft} size={20} className="mr-2" /> Back to Movies
        </Link>
      </div>
    );
  }

  return (
    <div className="min-h-screen pb-16">
      {/* Backdrop Header */}
      <div 
        className="relative h-[50vh] bg-cover bg-center"
        style={{ backgroundImage: `url(${movie.backdrop_path})` }}
      >
        <div className="absolute inset-0 bg-gradient-to-t from-black via-black/70 to-transparent"></div>
        <div className="absolute inset-0 flex flex-col justify-end px-4 py-12 sm:px-6 lg:px-8 container mx-auto">
          <div className="flex flex-col md:flex-row items-end md:items-end gap-6">
            <img 
              src={movie.poster_path} 
              alt={movie.title} 
              className="w-36 h-auto rounded-lg shadow-lg border-4 border-white relative z-10 hidden md:block" 
            />
            <div className="relative z-10 text-white">
              <h1 className="text-4xl font-bold mb-2">{movie.title}</h1>
              <div className="flex flex-wrap items-center gap-3 text-sm">
                <span>{new Date(movie.release_date).getFullYear()}</span>
                <span>•</span>
                <span>{formatRuntime(movie.runtime)}</span>
                <span>•</span>
                <div className="flex items-center">
                  <Icon icon={FiStar} size={16} className="mr-1 text-yellow-400" />
                  <span>{movie.vote_average.toFixed(1)}</span>
                </div>
              </div>
              <div className="mt-3 flex flex-wrap gap-2">
                {movie.genres.map((genre, index) => (
                  <span key={index} className="px-3 py-1 bg-blue-800/70 rounded-full text-sm">
                    {genre}
                  </span>
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Content */}
      <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="flex flex-col lg:flex-row gap-8">
          <div className="lg:w-2/3">
            <section className="mb-12">
              <h2 className="text-2xl font-bold text-gray-800 mb-4">Overview</h2>
              <p className="text-gray-700 leading-relaxed">{movie.overview}</p>
            </section>

            <section>
              <div className="flex justify-between items-center mb-6">
                <h2 className="text-2xl font-bold text-gray-800">Reviews</h2>
                <Link 
                  to={`/review/new?movieId=${movie.id}&movieTitle=${movie.title}`}
                  className="flex items-center px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg transition"
                >
                  <Icon icon={FiEdit} size={18} className="mr-2" />
                  Write a Review
                </Link>
              </div>

              {reviews.length === 0 ? (
                <div className="bg-gray-50 rounded-lg p-8 text-center">
                  <Icon icon={FiMessageSquare} size={36} className="mx-auto text-gray-400 mb-3" />
                  <h3 className="text-xl font-semibold text-gray-700 mb-2">No Reviews Yet</h3>
                  <p className="text-gray-600 mb-4">Be the first to share your thoughts on this movie!</p>
                  <Link 
                    to={`/review/new?movieId=${movie.id}&movieTitle=${movie.title}`}
                    className="inline-block px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg transition"
                  >
                    Write a Review
                  </Link>
                </div>
              ) : (
                <div className="space-y-6">
                  {reviews.map((review) => (
                    <div key={review._id} className="bg-white rounded-lg shadow-md p-6">
                      <div className="flex justify-between items-start mb-4">
                        <div className="flex items-center">
                          <div className="w-10 h-10 rounded-full bg-gradient-to-r from-blue-500 to-indigo-600 flex items-center justify-center text-white font-bold">
                            {review.user?.name ? review.user.name[0] : 'A'}
                          </div>
                          <div className="ml-3">
                            <div className="font-semibold text-gray-800">{review.user?.name || 'Anonymous User'}</div>
                            <div className="text-sm text-gray-500 flex items-center">
                              <Icon icon={FiClock} size={14} className="mr-1" />
                              {formatDate(review.createdAt)}
                            </div>
                          </div>
                        </div>
                        <div className="flex items-center space-x-2">
                          <div className="flex items-center bg-blue-100 text-blue-800 px-2 py-1 rounded">
                            <Icon icon={FiStar} size={14} className="mr-1" />
                            <span>{review.rating}/5</span>
                          </div>
                          <div className={`px-2 py-1 rounded text-sm ${getSentimentClass(review.sentiment)}`}>
                            <span>{review.sentiment}% Positive</span>
                          </div>
                        </div>
                      </div>
                      <p className="text-gray-700">{review.reviewText}</p>
                      <div className="mt-4">
                        <Link 
                          to={`/review/${review._id}`}
                          className="text-blue-600 hover:text-blue-800 font-medium"
                        >
                          Read full review
                        </Link>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </section>
          </div>

          {/* Sidebar */}
          <div className="lg:w-1/3">
            <div className="bg-white rounded-lg shadow-md p-6 mb-6">
              <h3 className="text-xl font-bold text-gray-800 mb-4">Movie Details</h3>
              <div className="space-y-3">
                <div>
                  <span className="text-gray-500">Release Date:</span>
                  <span className="block text-gray-800 font-medium">{formatDate(movie.release_date)}</span>
                </div>
                <div>
                  <span className="text-gray-500">Runtime:</span>
                  <span className="block text-gray-800 font-medium">{formatRuntime(movie.runtime)}</span>
                </div>
                <div>
                  <span className="text-gray-500">User Rating:</span>
                  <div className="flex items-center mt-1">
                    <Icon icon={FiStar} size={16} className="text-yellow-400 mr-1" />
                    <span className="text-gray-800 font-medium">{movie.vote_average.toFixed(1)}/10</span>
                  </div>
                </div>
              </div>
            </div>

            <div className="bg-gradient-to-r from-blue-600 to-indigo-700 rounded-lg shadow-md p-6 text-white">
              <h3 className="text-xl font-bold mb-4">Have you watched this movie?</h3>
              <p className="mb-6">Share your thoughts and help others discover great films!</p>
              <Link 
                to={`/movies`}
                className="block w-full py-2 bg-white text-blue-700 rounded-lg text-center font-medium hover:bg-blue-50 transition"
              >
                <Icon icon={FiStar} size={18} className="inline mr-2" />
                Discover More Movies
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MovieDetail;
